update restaurant
set location_id = 
case
	when address like '% 강서구 %' then 1
	when address like '% 사하구 %' then 2
	when address like '% 사상구 %' then 3
	when address like '% 북구 %' then 4
	when address like '% 서구 %' then 5
	when address like '% 중구 %' then 6
	when address like '% 동구 %' then 7
	when address like '% 부산진구 %' then 8
	when address like '% 영도구 %' then 9
	when address like '% 남구 %' then 10
	when address like '% 동래구 %' then 11
	when address like '% 연제구 %' then 12
	when address like '% 수영구 %' then 13
	when address like '% 금정구 %' then 14
	when address like '% 해운대구 %' then 15
	when address like '% 기장군 %' then 16
    end;
    
update restaurant
set open_time = 
	case
		when restaurant_id % 4 = 0 then '08:00:00'
		when restaurant_id % 4 = 1 then '09:00:00'
		when restaurant_id % 4 = 2 then '09:30:00'
		when restaurant_id % 4 = 3 then '10:00:00'
        end
where category_id = 2;

update restaurant
set open_time =
	case
		when restaurant_id % 4 = 0 then '11:00:00'
		when restaurant_id % 4 = 1 then '11:30:00'
		when restaurant_id % 4 = 2 then '12:00:00'
		when restaurant_id % 4 = 3 then '10:30:00'
        end
where category_id != 2
and category_id != 13;

update restaurant
set close_time =
	case
		when restaurant_id % 7 = 0 then '21:00:00'
		when restaurant_id % 7 = 1 then '21:30:00'
		when restaurant_id % 7 = 2 then '22:00:00'
		when restaurant_id % 7 = 3 then '22:30:00'
		when restaurant_id % 7 = 4 then '23:00:00'
		when restaurant_id % 7 = 5 then '23:30:00'
		when restaurant_id % 7 = 6 then '00:00:00'
        end;
        
update restaurant
set rating = round((rand() * 7) + 2, 1);
select * from restaurant;

update restaurant
set rest_day = 
	case
		when restaurant_id % 7 = 0 then '일요일'
		when restaurant_id % 7 = 1 then '월요일'
		when restaurant_id % 7 = 2 then '화요일'
		when restaurant_id % 7 = 3 then '수요일'
		when restaurant_id % 7 = 4 then '목요일'
		when restaurant_id % 7 = 5 then '금요일'
		when restaurant_id % 7 = 6 then '토요일'
        end;

